/* eslint-disable */
import "../App.css";
import React, { useRef } from "react";
import Breed from "./Breed";

function handleScroll() {
  const ref = useRef(null);
  ref.current?.scrollIntoView({ behavior: "smooth" });
}

var type = 0;
function handleClick(type) {
  type = type;
  handleScroll();
}

class Body extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      isToggleOn: true,
      type: 0,
    };
  }

  render() {
    return (
      <div>
        <div class="card-deck">
          <br />
          <h1 class="title">동물을 선택해주세요.</h1>
          <br />
          <div class="col d-flex justify-content-center">
            <div class="row">
              <div class="col-6">
                <div class="card" onClick={() => handleClick(1)}>
                  <img
                    class="card-img-top"
                    src="https://upload.wikimedia.org/wikipedia/commons/thumb/3/3a/Cat03.jpg/1200px-Cat03.jpg"
                    alt=""
                  />
                  <div class="card-body mx-auto">
                    <h5 class="card-title">고양이</h5>
                  </div>
                </div>
              </div>

              <div class="col-6">
                <div class="card" onClick={() => handleClick(2)}>
                  <img
                    src="https://cdn.crowdpic.net/detail-thumb/thumb_d_382A8A747FFDF073E20C13398D110DE7.jpg"
                    alt=""
                    class="card-img-top"
                  />
                  <div class="card-body mx-auto">
                    <h5 class="card-title">강아지</h5>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>

        <div style={{ height: "155rem" }} />
        <div ref={handleScroll.ref}>
          <Breed type={type}></Breed>
        </div>
        <div style={{ height: "155rem" }} />
      </div>
    );
  }
}

export default Body;
